function convertTemperature(){var o=$("#tempCelcius").val(),e=parseInt(1.8*o+32,10);updateHTML(e),updateBackground(e)}function updateHTML(o){$("#result").html(o)}function updateBackground(o){40>o?$("body").css("background-color","lightsteelblue"):70>o?$("body").css("background-color","blanchedalmond"):$("body").css("background-color","orange")}$("button").on("click",convertTemperature);
